def crearCliente(n):
        desc_nombres = ["Juan","Joaquin","Fabrizio", "Luciana","Matias","Mateo","Federico","Santiago","Lucia","Valentina","Victoria", "Ana", "Mar√≠a","Pedro","Josefina","Jose","Lucio","Patricia","Natalia"]
