def to_string(cliente):
    cadena = "DNI: " + str(cliente.dni) + "\t" \
             "nom y apellido: " + cliente.nombre_apellido + "\t" \
             "numero: " + str(cliente.nrocelular) + "\t" 
    return cadena

def mostrar_cliente(vector):
    cadena = "cliente \n"
    cadena += "=" * 80 + "\n"
